export default class Chat{init(){console.log('Chat initialized');if(!window?.shareYourSteps?.api_url)return;const data={message:'Hello'};fetch(window.shareYourSteps.api_url+'chat',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify(data)}).then(r=>r.json()).then(res=>console.log(res)).catch(err=>console.error(err));}}
