import Tracking from './tracking.min.js';import Chat from './chat.min.js';import LiveMode from './live-mode.min.js';import Filters from './filters.min.js';document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('.sys-map').forEach(function(el){const lat=el.dataset.lat;const lng=el.dataset.lng;const zoom=el.dataset.zoom;const map=L.map(el.id).setView([lat,lng],zoom);L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',{maxZoom:19,attribution:'&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'}).addTo(map);});new Tracking().init();new Chat().init();new LiveMode().init();new Filters().init();});
